// src/LoginForm.js

// Add this import at the top
import axios from 'axios';
import React, { useState } from 'react';
import { EyeIcon, EyeOffIcon } from './Icons';

const LoginForm = ({ onSwitchToSignup }) => {
  // MODIFIED: Changed 'email' to 'identifier'
  const [formData, setFormData] = useState({
    identifier: '',
    password: ''
  });
  const [errors, setErrors] = useState({});
  const [showPassword, setShowPassword] = useState(false);

  const validate = () => {
    const newErrors = {};
    // MODIFIED: Check 'identifier' field
    if (!formData.identifier) newErrors.identifier = 'Username or Email is required';
    if (!formData.password) newErrors.password = 'Password is required';
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };
  
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => { // Make the function async
    e.preventDefault();
    if (validate()) {
      // MODIFIED: Send 'identifier' to the backend
      const userCredentials = {
        identifier: formData.identifier,
        password: formData.password
      };

      try {
        // Send a POST request to the backend login endpoint
        const res = await axios.post('http://localhost:5001/api/auth/login', userCredentials);
        console.log(res.data);
        // You would typically save the token (res.data.token) here
        // e.g., localStorage.setItem('token', res.data.token);
        alert('Login successful!');
        // Here you would redirect to a dashboard or home page
      } catch (err) {
        console.error(err.response.data);
        alert('Login failed: ' + err.response.data.msg);
      }
    } else {
      console.log('Login validation failed');
    }
  };

  return (
    <div className="form-container">
      <div className="header">
        <div className="logo-placeholder"></div>
        <h1 className="title">MEMBER LOGIN</h1>
      </div>
      <form onSubmit={handleSubmit} noValidate>
        <div className="inputs-wrapper">
          <div>
            {/* MODIFIED: Changed name and value to 'identifier' */}
            <input
              type="text"
              name="identifier"
              placeholder="USERNAME OR EMAIL"
              value={formData.identifier}
              onChange={handleChange}
              className="input-field"
            />
            {errors.identifier && <p className="error-message">{errors.identifier}</p>}
          </div>
          <div className="password-wrapper">
            <input
              type={showPassword ? 'text' : 'password'}
              name="password"
              placeholder="PASSWORD"
              value={formData.password}
              onChange={handleChange}
              className="input-field"
            />
            <button type="button" onClick={() => setShowPassword(!showPassword)} className="password-toggle-btn">
              {showPassword ? <EyeOffIcon /> : <EyeIcon />}
            </button>
            {errors.password && <p className="error-message">{errors.password}</p>}
          </div>
        </div>
        <button type="submit" className="submit-btn">
          LOGIN
        </button>
      </form>
      <div className="switch-form-container">
        <button onClick={onSwitchToSignup} className="switch-form-btn">
          Create a new account? SIGNUP
        </button>
      </div>
    </div>
  );
};

export default LoginForm;
