// src/AdminDashboard.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '.'; // We can reuse the same CSS for now

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        // Get user info from localStorage
        const userInfo = JSON.parse(localStorage.getItem('userInfo'));
        const token = userInfo ? userInfo.token : null;

        if (!token) {
          setError('Not authorized. Please log in.');
          setLoading(false);
          return;
        }

        const config = {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        };

        const { data } = await axios.get('http://localhost:5001/api/admin/users', config);
        setUsers(data);
        setLoading(false);
      } catch (err) {
        setError(err.response ? err.response.data.msg : 'Error fetching users');
        setLoading(false);
      }
    };

    fetchUsers();
  }, []);

  const filteredUsers = users.filter(user =>
    user.username.toLowerCase().includes(searchTerm.toLowerCase()) ||
    user.email.toLowerCase().includes(searchTerm.toLowerCase())
  );

  if (loading) return <div className="app-background"><p>Loading...</p></div>;
  if (error) return <div className="app-background"><p style={{color: 'red'}}>{error}</p></div>;

  return (
    <div className="app-background">
      <div className="card" style={{ maxWidth: '800px' }}>
        <h1 className="title">Admin Console - Users</h1>
        
        <input
          type="text"
          placeholder="Search by username or email..."
          className="input-field"
          style={{ marginBottom: '1.5rem' }}
          onChange={(e) => setSearchTerm(e.target.value)}
        />

        <div style={{ overflowX: 'auto' }}>
          <table style={{ width: '100%', borderCollapse: 'collapse' }}>
            <thead>
              <tr style={{ borderBottom: '1px solid #a0aec0' }}>
                <th style={{ padding: '0.75rem', textAlign: 'left' }}>Username</th>
                <th style={{ padding: '0.75rem', textAlign: 'left' }}>Email</th>
                <th style={{ padding: '0.75rem', textAlign: 'left' }}>Role</th>
              </tr>
            </thead>
            <tbody>
              {filteredUsers.map(user => (
                <tr key={user._id} style={{ borderBottom: '1px solid #cbd5e0' }}>
                  <td style={{ padding: '0.75rem' }}>{user.username}</td>
                  <td style={{ padding: '0.75rem' }}>{user.email}</td>
                  <td style={{ padding: '0.75rem' }}>{user.role}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
