import React, { useState } from 'react';
import { EyeIcon, EyeOffIcon } from './Icons'; // Import icons

const LoginForm = ({ onSwitchToSignup }) => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [errors, setErrors] = useState({});
  const [showPassword, setShowPassword] = useState(false);

  const validate = () => {
    const newErrors = {};
    if (!formData.email) newErrors.email = 'Username or Email is required';
    if (!formData.password) newErrors.password = 'Password is required';
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validate()) {
      console.log('Login successful:', formData);
      alert('Login successful! Check the console for the form data.');
    } else {
      console.log('Login validation failed');
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  return (
    <div className="form-container">
      <div className="header">
        <div className="logo-placeholder"></div>
        <h1 className="title">MEMBER LOGIN</h1>
      </div>
      <form onSubmit={handleSubmit} noValidate>
        <div className="inputs-wrapper">
          <div>
            <input
              type="email"
              name="email"
              placeholder="USERNAME OR EMAIL"
              value={formData.email}
              onChange={handleChange}
              className="input-field"
            />
            {errors.email && <p className="error-message">{errors.email}</p>}
          </div>
          <div className="password-wrapper">
            <input
              type={showPassword ? 'text' : 'password'}
              name="password"
              placeholder="PASSWORD"
              value={formData.password}
              onChange={handleChange}
              className="input-field"
            />
            <button type="button" onClick={() => setShowPassword(!showPassword)} className="password-toggle-btn">
              {showPassword ? <EyeOffIcon /> : <EyeIcon />}
            </button>
            {errors.password && <p className="error-message">{errors.password}</p>}
          </div>
        </div>
        <button type="submit" className="submit-btn">
          LOGIN
        </button>
      </form>
      <div className="switch-form-container">
        <button onClick={onSwitchToSignup} className="switch-form-btn">
          Create a new account? SIGNUP
        </button>
      </div>
    </div>
  );
};

export default LoginForm;
